mapper.select:
select r.roomno ,wl.wishlistno, r.roomtype, r.roomsimplemanual, r.price, ri.roomimagepath
from room r, wishlist wl, roomimage ri
where r.roomno = wl.roomno and r.roomno = ri.roomno and wl.email = #{email}

select wl.roomno, r.roomtype, r.roomsimplemanual, r.roomdetailmanual, r.price, ri.roomimagepath 
from room r, wishlist wl, roomimage ri 
where r.roomno = wl.roomno and r.roomno = ri.roomno and wl.roomno = #{roomno}
